    //     }

    // }


    useEffect(() => {
        axios.get('http://localhost:8000/api/pets')
            .then(res => setPets(res.data))

    }, [])


    return (
        <div>
            <AllPets pets={pets}  filterPets={filterPets}/>
        </div>
    )
}

export default Home;
